cell.eg:
element charProp
{
   sga-patt.global.attributes,
   sga-patt.typed.attributes,
   ( ( unicodeName | localName ), value )
}

element desc
{
   sga-patt.global.attributes,
   sga-patt.translatable.attributes,
   macro.limitedContent
}

element div
{
   sga-patt.global.attributes,
   sga-patt.divLike.attributes,
   sga-patt.typed.attributes,
   (
      ( model.divTop | model.global )*,
      (
         (
            ( ( ( model.divLike | model.divGenLike ), model.global* )+ )
          | (
               ( ( model.common ), model.global* )+,
               ( ( model.divLike | model.divGenLike ), model.global* )*
            )
         ),
         ( ( model.divBottom ), model.global* )*
      )?
   )
}

element facsimile
{
   sga-patt.global.attributes,
   ( front?, ( model.graphicLike | surface | surfaceGrp )+, back? )
}

element item
{
   sga-patt.global.attributes,
   sga-patt.sortable.attributes,
   macro.specialPara
}

element linkGrp
{
   sga-patt.global.attributes,
   sga-patt.pointing.group.attributes,
   ( link | ptr )+
}

element mapping
{
   sga-patt.global.attributes,
   sga-patt.typed.attributes,
   macro.xtext
}

element num
{
   sga-patt.global.attributes,
   sga-patt.ranging.attributes,
   attribute type
   {
      "cardinal" | "ordinal" | "fraction" | "percentage" | xsd:Name
   }?,
   attribute value { data.numeric }?,
   macro.phraseSeq
}

element postscript
{
   sga-patt.global.attributes,
   (
      ( model.divTopPart*, model.common+, model.divBottomPart* )
    | ( model.global )
   )*
}

element resp
{
   sga-patt.global.attributes,
   sga-patt.canonical.attributes,
   macro.phraseSeq.limited
}

element respStmt
{
   sga-patt.global.attributes,
   ( ( resp+, model.nameLike.agent+ ) | ( model.nameLike.agent+, resp+ ) )
}

element sourceDoc
{
   sga-patt.global.attributes,
   ( ( model.global | model.graphicLike | surface | surfaceGrp )+ )
}

element subst
{
   sga-patt.global.attributes,
   sga-patt.transcriptional.attributes,
   ( add | del | model.milestoneLike )+
}

element unicodeName
{
   sga-patt.global.attributes,
   attribute version { data.version }?,
   text
}

data.outputMeasurement =
   token
   {
      pattern = "[\-+]?\d+(\.\d+)?(%|cm|mm|in|pt|pc|px|em|ex|gd|rem|vw|vh|vm)"
   }
